import Terminal from 'terminal-in-react';

export default class MyTerminal extends React.Component {
    
    showMsg = () => 'Hello World'
 
  render() {
    return (
      <div style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "93.4vh"
      }}>
        <Terminal
          color='green'
          backgroundColor='#1e2227'
          barColor='black'
          style={{ fontWeight: "bold", fontSize: "1em" ,
            "box-shadow": "0 4px 8px 0 rgba(0, 0, 0, 0.6), 0 6px 20px 0 rgba(0, 0, 0, 0.6)","overflow-x": "hidden", "overflow-y": "hidden"}}
          commands={{
            'open-google': () => window.open('https://www.google.com/', '_blank'),
            showmsg: this.showMsg,
            popup: () => alert('Terminal in React'),
            skills: () => `["Javascript", "React", "Node.js", "Next.js", "Express", "MongoDB", "Python", "C++", "HTML5", "Google Cloud Platform", "CSS3", "C", "PHP", "Wordpress"]`,
            projects: () => `["Cura (Build For Digital India)", "Rex", "Study Jamm", "Coretech"] \n length: 4`
          }}
          descriptions={{
            'open-google': 'opens google.com',
            showmsg: 'shows a message',
            alert: 'alert', popup: 'alert'
          }}
          msg= "Welcome aboard the Resolute! I am Rex, General Parakh's first in command. To begin with type 'help' to see the list of commands."
        />
      </div>
    );
  }
}
