import React from 'react'
import Layout from './../components/Layout';

const skills = ["Javascript", "React", "Node.js", "Next.js", "Express", "MongoDB", "Python", "HTML5", "Google Cloud Platform", "CSS3", "PHP", "Wordpress"]

export default class About extends React.Component {
    
    render() {
        return (
            <Layout>
                <div className="row">
                    <div className="col-md-6">
                        <img className="img-fluid pic" src="/images/demo.jpg" />
                    </div>
                    <div className="col-md-6">
                    {
                        skills.map(s => {
                            return(
                                <span className="badge badge-dark">{s}</span>
                            )
                        })
                    }
                    </div>
                </div>
                <style jsx>{`
                    .row{
                        margin: 2em;
                        text-align: justify;
                    }
                    .pic{
                        /*padding: 2em;*/
                        box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.6), 0 6px 20px 0 rgba(0, 0, 0, 0.6);
                        width: 100%;
                    }
                    .badge{
                        padding: 0.8em;
                        margin: 0.4em 1em 1em 1em !important;
                        font-size: 1rem;
                    }
                `}</style>
            </Layout>
        )
    }
}
